function animateLetter(n,t){var e=$(".num-"+n),a=e.clone();e.parent().append(a),e.animate({top:-t},function(){e.remove()}),a.animate({top:-t},function(){a.css({top:0})})}function animateLetters(n,t,e){var a=t();e.forEach(function(t){var e=Math.pow(3,t);a%e===0&&animateLetter(t,n)})}function clockify(n){for(var t=n.split(""),e=[];t.length;)e.unshift(t.pop()),t.length&&e.unshift(t.pop()),t.length&&e.unshift(":");return e.join("")}function insert(n,t){var e=$(".clock");n.split("").forEach(function(n){":"===n?e.append('<div><span class="colon">:</span></div>'):e.append('<div><span class="digit num-'+--t+'">'+n+"</span></div>")})}$(document).ready(function(){var n=1e3,t=50,e=0,a=window.location.search.slice(1)||"pizzatime",i=a.length;a=clockify(a),insert(a,i);for(var o=[];o.length<i;)o.push(o.length);setInterval(animateLetters,n,t,function(){return++e},o)});